var xhr = new XMLHttpRequest();
var url = "https://iot000.sonmar.com.ua/api/plugins/telemetry/RULE_CHAIN/8d5773d0-2f07-11ef-849b-699b159d4945/values/attributes/SERVER_SCOPE";
var token = localStorage.getItem("jwt_token");

xhr.open("GET", url, false);
xhr.setRequestHeader("Authorization", "Bearer " + token);
xhr.send();

if (xhr.status === 200) {
    var response = JSON.parse(xhr.responseText);

    // Проверка на наличие данных
    if (!response || response.length === 0) {
        return 'No data available';
    }

    // Найти все ключи, где есть слово "config"
    var configs = response.filter(item => item.key.includes("config"));

    // Если конфигурации не найдены
    if (configs.length === 0) {
        return 'No config data available';
    }

    // Запрос списка устройств
    var deviceXhr = new XMLHttpRequest();
    var deviceUrl = "https://iot000.sonmar.com.ua/api/deviceInfos/all?pageSize=1000&page=0&sortProperty=createdTime&sortOrder=DESC&includeCustomers=true";

    deviceXhr.open("GET", deviceUrl, false);
    deviceXhr.setRequestHeader("Authorization", "Bearer " + token);
    deviceXhr.send();

    var deviceListHTML = '';
    if (deviceXhr.status === 200) {
        var deviceResponse = JSON.parse(deviceXhr.responseText);
        var devices = deviceResponse.data;

        if (!devices || devices.length === 0) {
            deviceListHTML = '<p>No devices available</p>';
        } else {
            deviceListHTML = '<input list="deviceDropdown" id="deviceSearch" placeholder="Search devices(double click)"><datalist id="deviceDropdown">';
            devices.forEach(device => {
                deviceListHTML += `<option value="${device.name}">${device.name} (${device.type})</option>`;
            });
            deviceListHTML += '</datalist><button id="deleteDeviceButton">Delete Selected Device</button>';
        }
    } else {
        deviceListHTML = "<p>Error fetching device list.</p>";
    }

    // Построение таблиц для каждой конфигурации
    let allTablesHTML = deviceListHTML; // Добавляем список устройств в начало

    configs.forEach((config, configIndex) => {
        // Проверка, является ли значение валидным JSON
        try {
            let configData = JSON.parse(config.value.replace(/'/g, '"'));

            let tableHTML = `<h3>${config.key}</h3><table border="1"><tr><th>Category</th><th>Values</th></tr>`;

            for (const [category, values] of Object.entries(configData)) {
                let valuesHTML = '';
                if (Array.isArray(values)) {
                    valuesHTML += '<details><summary>Values</summary>';
                    values.forEach((value, index) => {
                        let inputId = `${config.key}_${category}_${value}`;
                        valuesHTML += `<div>${value}: <input type="text" id="${inputId}" /></div>`;
                    });
                    valuesHTML += '</details>';
                } else {
                    let inputId = `${config.key}_${category}_${values}`;
                    valuesHTML += `<div>${values}: <input type="text" id="${inputId}" />${category === 'config' ? ` <button id="${config.key}_category_button" class="config-button">Fill th inputs</button>` : ''}</div>`;
                }
                tableHTML += `<tr>
                                <td>${category}</td>
                                <td>${valuesHTML}</td>
                              </tr>`;
            }

            tableHTML += '</table>';
            allTablesHTML += tableHTML;
        } catch (error) {
            console.error('Skipping invalid JSON:', config.value);
        }
    });

    // Добавление кнопок в конец HTML
    allTablesHTML += `
        <div class="button-container">
            <button id="actionButton">Confirm</button>
            <button id="clearInputsButton">Clear All Inputs</button>
        </div>`;

    return allTablesHTML;
} else {
    return "Error fetching telemetry data.";
}

