    let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

    // Получить значение из Input
    const rateValue = document.getElementById('rateInput').value;

    if (!rateValue) {
        showNotification('Please enter a value.');
        return;
    }

    // Формирование тела запроса
    const attributes = [
        {
            key: 'telemetry_rate_sec',
            value: rateValue
        }
    ];

    // Отправка запроса для установки атрибута
    attributeService.saveEntityAttributes(
        { entityType: 'DEVICE', id: entityId.id },
        'SHARED_SCOPE',
        attributes
    ).subscribe(
        response => {
            console.log('Attribute saved successfully', response);
            showNotification('Attribute saved successfully.');
        },
        error => {
            console.error('Error saving attribute', error);
            showNotification('Error saving attribute.');
        }
    );

    // Функция для отображения уведомления
    function showNotification(message) {
        const notification = document.createElement('div');
        notification.style.position = 'fixed';
        notification.style.top = '50%';
        notification.style.left = '50%';
        notification.style.transform = 'translate(-50%, -50%)';
        notification.style.padding = '20px';
        notification.style.backgroundColor = '#4caf50';
        notification.style.color = '#fff';
        notification.style.fontSize = '18px';
        notification.style.borderRadius = '8px';
        notification.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
        notification.style.zIndex = '1000';
        notification.textContent = message;

        document.body.appendChild(notification);

        setTimeout(() => {
            document.body.removeChild(notification);
        }, 3000);
    }
