attributeService.getEntityAttributes({ entityType: 'DEVICE', id: entityId.id }, 'SHARED_SCOPE', ['configFromTB'])
    .subscribe(
        response => {
            const configFromTB = response.find(item => item.key === 'configFromTB');
            if (!configFromTB) {
                showNotification('Config not found.');
                return;
            }

            let mergedConfig = JSON.parse(configFromTB.value);

            // Проверить, существует ли устройство с данным именем
            if (!mergedConfig[deviceName]) {
                showNotification(`Device "${deviceName}" not found in the configuration.`);
                return;
            }

            // Удалить устройство из конфигурации
            delete mergedConfig[deviceName];

            // Сохранить обновленную конфигурацию
            const attributes = [
                {
                    key: 'configFromTB',
                    value: JSON.stringify(mergedConfig)
                }
            ];

            attributeService.saveEntityAttributes(
                { entityType: 'DEVICE', id: entityId.id },
                'SHARED_SCOPE',
                attributes
            ).subscribe(
                response => {
                    console.log('Attributes saved successfully', response);
                    showNotification(`Device "${deviceName}" has been deleted successfully.`);
                },
                error => {
                    console.error('Error saving attributes', error);
                    showNotification('Error saving attributes.');
                }
            );
        },
        error => {
            console.error('Error fetching configFromTB', error);
            showNotification('Error fetching configFromTB.');
        }
    );

