return '<span style="font-size: 18px; color: ' + color + '">&#11044;</span>';

settings.markdownTextFunction:
var idGateway = data[0].$datasource.entityId;
var baseUrl = "https://iot000.sonmar.com.ua";
var token = localStorage.getItem("jwt_token");

// Новый запрос для списка устройств
var deviceXhr = new XMLHttpRequest();
var deviceUrl = `${baseUrl}/api/deviceInfos/all?pageSize=2000&page=0&sortProperty=createdTime&sortOrder=DESC&includeCustomers=true`;

deviceXhr.open("GET", deviceUrl, false);
deviceXhr.setRequestHeader("Authorization", "Bearer " + token);
deviceXhr.send();

var ruleChainListHTML = '';
var filteredDevices = [];
if (deviceXhr.status === 200) {
    var deviceResponse = JSON.parse(deviceXhr.responseText);
    var devices = deviceResponse.data;

    // Фильтрация устройств: оставить только те, у которых есть группа "non-gateway bound"
    filteredDevices = devices.filter(device => device.groups && device.groups.some(group => group.name === "non-gateway bound"));

    if (!filteredDevices || filteredDevices.length === 0) {
        ruleChainListHTML = '<p>No devices available in non-gateway bound group</p>';
    } else {
        ruleChainListHTML = `
            <input style="margin-bottom: 10px; padding: 10px; width: calc(100% - 20px); box-sizing: border-box;" list="ruleChainDropdown" id="ruleChainSearch" placeholder="Search devices">
            <datalist id="ruleChainDropdown">
        `;
        filteredDevices.forEach(device => {
            var displayName = device.name;
            ruleChainListHTML += `<option value="${displayName}" data-id="${device.id.id}">${displayName}</option>`;
        });
        ruleChainListHTML += '</datalist>';
    }
} else {
    ruleChainListHTML = "<p>Error fetching device list.</p>";
}

ruleChainListHTML += `
    <button id="linkDeviceButton" onclick="linkSelectedDevice()" style="background-color: #007BFF; color: white; border: none; padding: 10px 20px; text-align: center; text-decoration: none; display: inline-block; font-size: 16px; margin: 4px 2px; cursor: pointer; border-radius: 4px; transition: background-color 0.3s;">Connect New Device</button>`;

// Функция для привязки выбранного устройства
window.linkSelectedDevice = function() {
    var selectedDeviceName = document.querySelector('#ruleChainSearch').value;
    var selectedOption = Array.from(document.querySelectorAll('#ruleChainDropdown option')).find(option => option.value === selectedDeviceName);

    if (selectedOption) {
        var selectedDeviceId = selectedOption.getAttribute('data-id');

        // Удаление устройства из группы "non-gateway bound"
        removeFromNonGatewayBoundGroup([selectedDeviceId]);

        // Добавление устройства в группу "gateway-bound"
        addToGatewayBoundGroup([selectedDeviceId]);

        var postBody = {
            from: {
                entityType: "DEVICE",
                id: idGateway
            },
            to: {
                entityType: "DEVICE",
                id: selectedDeviceId
            },
            type: "Created",
            typeGroup: "COMMON"
        };

        var xhr = new XMLHttpRequest();
        var url = `${baseUrl}/api/relation`;

        xhr.open("POST", url, true);
        xhr.setRequestHeader("Content-Type", "application/json;charset=UTF-8");
        xhr.setRequestHeader("Authorization", "Bearer " + token);

        xhr.onreadystatechange = function() {
            if (xhr.readyState === 4) {
                if (xhr.status === 200) {
                    console.log('Success:', xhr.responseText);
                    showCustomNotification("Success", "Device linked successfully!", "success");

                    // Удаляем устройство из списка
                    filteredDevices = filteredDevices.filter(device => device.id.id !== selectedDeviceId);
                    updateDeviceList();

                    // Если это было последнее устройство, очищаем список и ввод
                    if (filteredDevices.length === 0) {
                        document.querySelector('#ruleChainSearch').value = '';
                        document.getElementById('ruleChainDropdown').innerHTML = '';
                    }
                } else {
                    console.error('Error:', xhr.responseText);
                    showCustomNotification("Error", "Failed to link device.", "error");
                }
            }
        };

        xhr.send(JSON.stringify(postBody));
    } else {
        showCustomNotification("Error", "No valid device selected.", "error");
    }
};

// Функция для обновления списка устройств после привязки
function updateDeviceList() {
    var datalist = document.getElementById('ruleChainDropdown');
    datalist.innerHTML = '';

    filteredDevices.forEach(device => {
        var displayName = device.name;
        datalist.innerHTML += `<option value="${displayName}" data-id="${device.id.id}">${displayName}</option>`;
    });
}

// Функция для удаления устройства из группы "non-gateway bound"
function removeFromNonGatewayBoundGroup(deviceIds) {
    var groupXhr = new XMLHttpRequest();
    var groupUrl = `${baseUrl}/api/entityGroups/DEVICE?pageSize=1000&page=0&sortProperty=createdTime&sortOrder=DESC&includeShared=false`;

    groupXhr.open("GET", groupUrl, false);
    groupXhr.setRequestHeader("Authorization", "Bearer " + token);
    groupXhr.send();

    if (groupXhr.status === 200) {
        var groupResponse = JSON.parse(groupXhr.responseText);
        var nonGatewayBoundGroup = groupResponse.data.find(group => group.name === "non-gateway bound");

        if (nonGatewayBoundGroup) {
            var nonGatewayBoundGroupId = nonGatewayBoundGroup.id.id;

            var deleteEntitiesXhr = new XMLHttpRequest();
            var deleteEntitiesUrl = `${baseUrl}/api/entityGroup/${nonGatewayBoundGroupId}/deleteEntities`;

            deleteEntitiesXhr.open("POST", deleteEntitiesUrl, true);
            deleteEntitiesXhr.setRequestHeader("Content-Type", "application/json;charset=UTF-8");
            deleteEntitiesXhr.setRequestHeader("Authorization", "Bearer " + token);

            deleteEntitiesXhr.onreadystatechange = function() {
                if (deleteEntitiesXhr.readyState === 4) {
                    if (deleteEntitiesXhr.status === 200) {
                        console.log("Device removed from non-gateway bound group successfully!");
                    } else {
                        showCustomNotification("Error", "Failed to remove device from non-gateway bound group.", "error");
                    }
                }
            };

            deleteEntitiesXhr.send(JSON.stringify(deviceIds));
        } else {
            showCustomNotification("Error", "non-gateway bound group not found.", "error");
        }
    } else {
        showCustomNotification("Error", "Failed to fetch groups.", "error");
    }
}

// Функция для добавления устройства в группу "gateway-bound"
function addToGatewayBoundGroup(deviceIds) {
    var groupXhr = new XMLHttpRequest();
    var groupUrl = `${baseUrl}/api/entityGroups/DEVICE?pageSize=1000&page=0&sortProperty=createdTime&sortOrder=DESC&includeShared=false`;

    groupXhr.open("GET", groupUrl, false);
    groupXhr.setRequestHeader("Authorization", "Bearer " + token);
    groupXhr.send();

    if (groupXhr.status === 200) {
        var groupResponse = JSON.parse(groupXhr.responseText);
        var gatewayBoundGroup = groupResponse.data.find(group => group.name === "gateway-bound");

        if (gatewayBoundGroup) {
            var gatewayBoundGroupId = gatewayBoundGroup.id.id;

            var addEntitiesXhr = new XMLHttpRequest();
            var addEntitiesUrl = `${baseUrl}/api/entityGroup/${gatewayBoundGroupId}/addEntities`;

            addEntitiesXhr.open("POST", addEntitiesUrl, true);
            addEntitiesXhr.setRequestHeader("Content-Type", "application/json;charset=UTF-8");
            addEntitiesXhr.setRequestHeader("Authorization", "Bearer " + token);

            addEntitiesXhr.onreadystatechange = function() {
                if (addEntitiesXhr.readyState === 4) {
                    if (addEntitiesXhr.status === 200) {
                        showCustomNotification("Success", "Device linked successfully!", "success");
                    } else {
                        showCustomNotification("Error", "Failed to add device to gateway-bound group.", "error");
                    }
                }
            };

            addEntitiesXhr.send(JSON.stringify(deviceIds));
        } else {
            showCustomNotification("Error", "gateway-bound group not found.", "error");
        }
    } else {
        showCustomNotification("Error", "Failed to fetch groups.", "error");
    }
}

// Функция для показа кастомного уведомления
function showCustomNotification(title, message, type) {
    const notification = document.createElement('div');
    notification.style.position = 'fixed';
    notification.style.top = '50%';
    notification.style.left = '50%';
    notification.style.transform = 'translate(-50%, -50%)';
    notification.style.padding = '20px';
    notification.style.backgroundColor = type === "success" ? '#4caf50' : '#f44336';
    notification.style.color = '#fff';
    notification.style.fontSize = '18px';
    notification.style.borderRadius = '8px';
    notification.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
    notification.style.zIndex = '1000';
    notification.style.textAlign = 'center';

    const notificationTitle = document.createElement('h3');
    notificationTitle.textContent = title;
    notificationTitle.style.margin = '0 0 10px 0';

    const notificationMessage = document.createElement('p');
    notificationMessage.textContent = message;
    notificationMessage.style.margin = '0';

    notification.appendChild(notificationTitle);
    notification.appendChild(notificationMessage);

    document.body.appendChild(notification);

    setTimeout(() => {
        document.body.removeChild(notification);
    }, 3000);
}

return ruleChainListHTML;
